package patterns;

public class pattern10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		

//     	1
//    	212
//   	32123
//      4321234
// 	    543212345
// 
//		
		
		
		for (int i=1; i<=5; i++){
			
			for (int k=i; k>=1; k--){
		  System.out.print(k);           
			}                                        
		  for (int l=2; l<=i; l++){
		  System.out.print(l);   
			}                            
			System.out.println();
			}                                                 
			
		
		
		
		
		
		

		
		
		
		
		
		
	}

}
