package patterns;

public class pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//		[8]
//		 
//      *
//     	***
//    	*****
//    	*******
//    	*********

		
		
		
		int i,j,k;
		
		for(i=1;i<=5;i++) {
			k=2*i-1;
			for(j=1;j<=k;j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
